import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';

contractAddress = "0x2eba62f4ebf9452783e3d27c1de94865ba5d2d48"
ABIArray = [{"constant":true,
			 "inputs":[],"name":"name",
			 "outputs":[{"name":"","type":"string"}],
			 "payable":false,"type":"function"},
			{"constant":false,
			 "inputs":[{"name":"nextName","type":"string"}],
			 "name":"changeName",
			 "outputs":[],
			 "payable":false,"type":"function"}]
data = ""

myContract = web3.eth.contract(ABIArray).at(contractAddress);

Template.hello.onCreated(function helloOnCreated() {
  // counter starts at 0
  this.counter = new ReactiveVar(0);
});

Template.hello.helpers({
  counter() {
  	var template = Template.instance();
  	
  	myContract.name(function(err, res){
  		TemplateVar.set(template, "counter", res)
  	})
  },
});

Template.hello.events({
  'click button'(event, instance) {
  		myContract.changeName(document.getElementById("name").value, function(err, res){
  			if(!err) console.log(res);
  			else console.log(err);
  		});
  		document.getElementById("name").value = "";
  },
});
